import React from 'react'
import Layout from '../components/Layout'
import '../App.css'
import AuthContext from '../AuthContext'
import { Link } from 'react-router-dom'
import axios from 'axios'

function TasksAddPage(props) {

  const token = React.useContext(AuthContext)
  const [quizLayout, setQuizLayout] = React.useState()
  const [questions, setQuestions] = React.useState([{
      description: '',
      type: '',
      choices: []
  }])

  var taskgroup_id;
  if (props.location.taskgroup_id != undefined) {
    localStorage.setItem('currentTaskgroup', props.location.taskgroup_id)
    taskgroup_id = props.location.taskgroup_id
  } else {
    taskgroup_id = localStorage.getItem('currentTaskgroup')
  }

  function handleSubmit(event) {
    event.preventDefault()
    console.log(event.target)
    const name = event.target[0].value
    const difficulty = event.target[1].value
    const description = event.target[2].value
    const hint = event.target[3].value
    const solution = event.target[4].value

    axios.post('/tasks/add', { token, taskgroup_id, name, difficulty, description, hint, solution })
      .then((response) => {
        const data = response.data
        console.log(data)
        props.history.push('/topics/manage')
      })
      .catch((err) => {})
  }

  function changeLayout() {
      var element = document.getElementById('type')
      if (element.value == 'quiz') {
          console.log('Quiz layout')
          setQuizLayout(true)
      } else {
          setQuizLayout(false)
      }
  }

  function addQuestion() {
      document.getElementById('addQuestionButton').style.display = 'none'
      document.getElementById('questionCreate').style.display = 'block'
  }

  function createQuestion(event) {
      const description = event.target[0].value
      const type = event.target[1].value

      questions.push({description: description, type: type, choices: []})
      setQuestions(questions)
  }

  function questionContent(type, choices, questionNo) {
      if (type == 'multiple choice') {
          return (<>
              {choices.map((choice, index) => (
                  <>
                    <input type='radio' value={index} id={'question' + questionNo + index} />
                    <label for='radio'>{choice}</label>
                    <div className='minusButton'>-</div>s
                  </>
              ))}
              <div className='plusButton'>+</div>
          </>)
      }
  }

  var bodySection;
  if (!quizLayout) {
      bodySection = (<>
        <textarea name='description' placeholder="Description"></textarea><br/><br/>
        <textarea name='hint' placeholder="Hint"></textarea><br/><br/>
        <textarea name='solution' placeholder="Solution"></textarea><br/><br/></>)
  } else {
      bodySection = (
         <div id='quizQuestions'>
            <h4>Questions</h4>
            {questions.map(({description, type, choices}, index) => (
                <div className='question'>
                    <h4>Question {index + 1}</h4><br/>
                    <textarea name='description' defaultValue={description} /><br/>
                    {questionContent(type, choices, index + 1)}
                    <div className='redTeacherButton'>Remove Question</div>
                </div>
            ))}
            <div className='greenTeacherButton' id='addQuestionButton' onClick={addQuestion}>Add Question</div>

            <div id='questionCreate' style='display: none'>
                <h4>New Question</h4>
                <form name='questionCreate' onSubmit={createQuestion}>
                    <textarea name='description' placeholder='Description' /><br/>
                    <select id='newQuestionType'>
                        <option value='multiple choice single'>Multiple Choice (Single Answer)</option>
                        <option value='multiple choice multi'>Multiple Choice (Multiple Answers)</option>
                        <option value='short answer'>Short Answer</option>
                    </select>
                    <input type='submit' value='Create Question' />
                </form>
            </div>
            <h4>End of Questions</h4>
         </div>
      )
  }

  return (
    <Layout navtype='teacher' body={
      <>
        <div class='headerSection'>
            <h1>Tasks</h1>
            <h2>Topic: {props.location.topic_name}</h2>
            <h2>Task Group: {props.location.taskgroup_name}</h2>
        </div>
        <hr/>

        <form name='addTask' onSubmit={handleSubmit}>
          <input type='text' placeholder='*Title' name='name' /><br/><br/>

          <h4>Task Type</h4><br/>
          <select id='type' onChange={changeLayout}>
            <option value='standard'>Standard</option>
            <option value='content'>Content only (no answer)</option>
            <option value='quiz'>Quiz</option>
          </select>
          <br/>

          <h4>*Difficulty</h4><br/>

          <select id="difficulty">
              <option value="Bronze">Bronze</option>
              <option value="Silver">Silver</option>
              <option value="Gold">Gold</option>
              <option value='Platinum'>Platinum</option>
              <option value='Kryptonite'>Kryptonite</option>
          </select><br/><br/>

          {bodySection}

          <input type='submit' value='Create Task' />
          <Link to='/topics/manage'>Cancel</Link>
        </form>
      </>
    }/>
  )
}

export default TasksAddPage;
