import React from 'react'
import Layout from '../components/Layout'
import '../App.css'
import AuthContext from '../AuthContext'
import { Link } from 'react-router-dom'
import axios from 'axios'
import { useStep } from '../utils/update'

function TasksEditPage(props) {

  const [task, setTask] = React.useState({
    task_id: 0,
    taskgroup: "",
    name: "",
    difficulty: "",
    description: "",
    hint: "",
    solution: ""
  })
  const token = React.useContext(AuthContext)

  var task_id;
  if (props.location.task_id != undefined) {
    localStorage.setItem('currentTask', props.location.task_id)
    task_id = props.location.task_id
  } else {
    task_id = localStorage.getItem('currentTask')
  }

  const fetchTaskData = () => {
    const getTask = axios.get('/tasks/details', {params: { token, task_id }})
      .then((taskResponse) => {
        const taskData = taskResponse.data.task
        console.log(taskData)
        setTask(taskData)
      })
  }

  const step = useStep(fetchTaskData, [], 2)

  function handleSubmit(event) {
    event.preventDefault()
    console.log(event.target)
    const name = event.target[0].value
    const difficulty = event.target[1].value
    const description = event.target[2].value
    const hint = event.target[3].value
    const solution = event.target[4].value

    axios.post('/tasks/edit', { token, task_id, name, difficulty, description, hint, solution })
      .then((response) => {
        const data = response.data
        console.log(data)
        props.history.push('/topics/manage')
      })
      .catch((err) => {})
  }

  return (
    <Layout navtype='teacher' body={
      <>
        <div class='headerSection'>
            <h1>Tasks</h1>
            <h2>Topic: {props.location.topic_name}</h2>
            <h2>Task Group: {props.location.taskgroup_name}</h2>
        </div>
        <hr/>

        <form name='addTask' onSubmit={handleSubmit}>
          <input type='text' placeholder='*Title' name='name' value={task.name}/><br/><br/>
          <h4>*Difficulty</h4>

          <select id="difficulty">
              <option value="Bronze">Bronze</option>
              <option value="Silver">Silver</option>
              <option value="Gold">Gold</option>
              <option value='Platinum'>Platinum</option>
              <option value='Kryptonite'>Kryptonite</option>
          </select><br/><br/>

          <textarea name='description' placeholder="Description" value={task.description}></textarea><br/><br/>

          <textarea name='hint' placeholder="Hint" value={task.hint}></textarea><br/><br/>

          <textarea name='solution' placeholder="Solution" value={task.solution}></textarea><br/><br/>

          <input type='submit' value='Update Task' />
          <Link to='/topics/manage'>Cancel</Link>
        </form>
      </>
    }/>
  )
}

export default TasksEditPage;
