import React from 'react'

function Layout({navtype, body, ...props}) {
  var navbar;

  const setDestination = (path) => props.history.push(path);

  if (navtype == 'teacher') {
    navbar = <>
      <h4 onClick={setDestination('/teacher')}>Dashboard</h4>
      <h4 onClick={setDestination('/courses')}>Courses</h4>
      <h4 onClick={setDestination('/classes')}>Classes</h4>
      <h4 onClick={setDestination('/topics')}>Topics</h4>
    </>
  }

  return (
    <>
      <div id='navbar'><h3>Gallifrey</h3></div>
      {body}
      <div className='footer'>
        Gallifrey 4.0.0<br />
        Developed By <a href='mailto:nick.p@iinet.net.au'>Nick Patrikeos</a>.
      </div>
    </>
  );
}

export default Layout
