import React from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom';
import Layout from '../components/Layout'
import '../App.css'
import AuthContext from '../AuthContext'
import { useStep } from '../utils/update'
import { Converter } from 'showdown'
import ReactHTMLParser from 'react-html-parser'

function SubmissionsMarkPage(props) {

  var submission_id
  if (props.location.submission_id != undefined) {
    submission_id = props.location.submission_id
    localStorage.setItem('currentSubmission', submission_id)
  } else {
    submission_id = localStorage.getItem('currentSubmission')
  }

  const token = React.useContext(AuthContext)
  const [submission, setSubmission] = React.useState({
    submission_id: 0,
    comment: '',
    status: '',
    task: {
      description: ''
    },
    url: ''
  })
  const [textBox, setTextBox] = React.useState(ReactHTMLParser(converter.makeHtml(submission.task.description)))

  const fetchSubmissionData = () => {
    const getSubmissions = axios.get('/submissions/details', {params: { token, submission_id }})
      .then((submissionsResponse) => {
        const submissionData = submissionsResponse.data.submission
        console.log(submissionData)
        setSubmission(submissionData)
      })
      .catch((err) => {})
  }

  function showTask() {
    setTextBox(ReactHTMLParser(converter.makeHtml(submission.task.solution)))
  }

  const step = useStep(fetchSubmissionData, [], 1)
  var converter = new Converter()

  return (
    <Layout navtype='teacher' body={
      <>
        <div className='headerSection'>
          <h1>Submissions</h1>
        </div>
        <hr/>
        <div className='viewSubmission'>
          <h4>Submission</h4>
          <a href={submission.url}>Download</a>
          <div className='teacherButton' id='closeWindow'>
            Reset Marking
          </div>

          <div id='topicTabs'>
            <div className='topicTab' onClick={showTask}>Task</div>
            <div className='topicTab' onClick={showSolution}>Solution</div>
          </div>

          <div className='greenTeacherButton'>Correct</div>
          <div className='redTeacherButton'>Incorrect</div>
          <div id='taskSolutionContent'>
            {textBox}
          </div>
          <h4>Comments</h4>
          <form name='addCommentForm'>
            <input type='text' name='comment' defaultValue={submission.comment}/>
            <input type='submit' value='Add Comment'/>
          </form>
        </div>
      </>
    }/>
  )

}

export default SubmissionsMarkPage
