import React from 'react'
import { Link } from 'react-router-dom'
import AuthContext from '../AuthContext'
import axios from 'axios'

function Layout({navtype, body}, ...props) {
  var navbar;

  const token = React.useContext(AuthContext)

  function logout() {
    axios.post('/auth/logout', {token})
      .then((response) => {
        console.log(response)
        window.location.href = '/login'
      })
  }

  if (navtype == 'teacher') {
    return (
      <>
        <div id='navbar'>
          <h3>Gallifrey</h3>
          <h4><Link to='/teacher'>Dashboard</Link></h4>
          <h4><Link to='/courses'>Courses</Link></h4>
          <h4><Link to='/classes'>Classes</Link></h4>
          <h4><Link to='/topics'>Topics</Link></h4>
          <div name='logout'><Link onClick={logout}>LOGOUT</Link></div>
        </div>

        <div id='error'></div>
        {body}
        <div className='footer'>
          Gallifrey 4.0.0<br />
          Developed By <a href='mailto:nick.p@iinet.net.au'>Nick Patrikeos</a>.
        </div>
      </>
    );
  } else {
    return (
      <>
        <div id='navbar'><h3>Gallifrey</h3></div>
        <div id='error'></div>
        {body}
        <div className='footer'>
          Gallifrey 4.0.0<br />
          Developed By <a href='mailto:nick.p@iinet.net.au'>Nick Patrikeos</a>.
        </div>
      </>
    );
  }
}

export default Layout
