import React from 'react'
import Layout from '../components/Layout'
import '../App.css'
import AuthContext from '../AuthContext'
import { Link } from 'react-router-dom'
import axios from 'axios'
import { useStep } from '../utils/update'

function CoursesViewPage(props) {

  var course_id
  if (props.location.course_id != undefined) {
    course_id = props.location.course_id
    localStorage.setItem('currentCourse', props.location.course_id)
  } else {
    course_id = localStorage.getItem('currentCourse')
  }

  const [course, setCourse] = React.useState({
    course_id: 0,
    name: "",
    modules: []
  })

  const token = React.useContext(AuthContext)

  const fetchCourseData = () => {
    const getCourse = axios.get('/courses/details', {params: { token, course_id }})
      .then((courseResponse) => {
        const courseData = courseResponse.data.course
        console.log(courseData)
        setCourse(courseData)
      })
      .catch((err) => {})
  }

  function showTopic(module_id) {}

  const step = useStep(fetchCourseData, [], 2)

  return (
    <Layout navtype='teacher' body={
      <>
        <div>
            <h1 id='courseName'>{course.name}</h1>
        </div>

        <hr/>

        <table id='topicProgress'>
          <tr>
          {course.modules.map(({ module_name }, index) => (
            <td>{module_name}</td>
          ))}
          </tr>
          <tr id='progressDashRow'>

          </tr>

        </table>

        <div id='topicTabs'>
        {course.modules.map(({ module_id, module_name, topic_id }, index) => (
          <div class='topicTab' id={module_id} onClick={() => showTopic(module_id)}>
            {module_name}
          </div>
        ))}
        </div>

        <div>
            <div id='taskgroupList'>
            </div>

            <div id='taskBox'>
            </div>
        </div>
    </>
  }/>
  )

}

export default CoursesViewPage
