import React from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom';
import '../App.css'
import AuthContext from '../AuthContext'
import { useStep } from '../utils/update'

function TopicsList() {
  const [topics, setTopics] = React.useState([])

  const token = React.useContext(AuthContext)

  const fetchTopicsData = () => {
    const getTopics = axios.get('/topics/list', {params: { token }})
      .then((topicResponse) => {
        const topicData = topicResponse.data.topics
        setTopics(topicData)
      }
    );
  }

  const step = useStep(fetchTopicsData, [], 2)

  return (
    <>
      <table id='topicsList'>
        {topics.map(({ topic_id, name}, index) => (
          <tr>
            <td>{name}</td><td>Courses</td>
            <td>
              <Link to='/topic/manage'>Manage</Link>
              <Link to='/topic/edit'>Edit</Link>
              <Link to='/topic/duplicate'>Duplicate</Link>
              <Link to='/topic/delete'>Delete</Link>
            </td>
          </tr>
        ))}
      </table>
    </>
  );
}

export default TopicsList;
