import React from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import '../App.css'
import AuthContext from '../AuthContext'
import { useStep } from '../utils/update'

function Manage({ type, object_id }, ...props) {
  var headerTitle;
  const [topic, setTopic] = React.useState({ name: "", topic_id: 0, taskgroups: []})
  const token = React.useContext(AuthContext)

  console.log(object_id)
  const fetchTopicData = () => {
    const topic_id = object_id
    const getTopic = axios.get('/topics/details', {params: { token, topic_id }})
    .then((topicResponse) => {
      const topicData = topicResponse.data.topic
      console.log(topicData)
      setTopic(topicData)
      console.log(topic)
    })
  }

  function deleteTaskGroup(taskgroup_id) {
    axios.delete('/taskgroups/delete', {data: { token, taskgroup_id }})
      .then((response) => {
        const data = response.data
        window.location.reload()
      })
      .catch((err) => {})
  }

  function deleteTask(task_id) {
    axios.delete('/tasks/delete', {data: { token, task_id }})
      .then((response) => {
        const data = response.data
        window.location.reload()
      })
      .catch((err) => {})
  }

  const step = useStep(fetchTopicData, [], 2)

  if (type == 'topic') {
    headerTitle = 'Topic Management'
  } else {}

  return (
    <>
      <div className='headerSection'>
        <h1>{headerTitle}</h1>
      </div>

      <h4>Title</h4>
      <p id='topicTitle'>{topic.name}</p>
      <h4>Courses</h4>
      <p id='topicCourses'>##TODO</p>

      <h2>Task Groups</h2>
      <Link to={{
        pathname: '/taskgroups/add',
        topic_id: object_id,
        topic_name: topic.name
      }}>
        <div class='teacherButton'>New Task Group</div>
      </Link>
      <div class='teacherButton' id='reorderTaskGroupsButton'>Reorder Task Groups</div>

      <hr />

      <table id='taskGroupList'>
          <tr><td>Title</td><td>Tasks</td><td>Actions</td></tr>
          {topic.taskgroups.map(({ taskgroup_id, name, tasks}, index) => (
            <tr>
              <td>{name}</td>
              <td>{tasks.length}</td>
              <td>
                <Link to={{
                  pathname: '/taskgroups/edit',
                  topic_name: topic.name,
                  taskgroup_id: taskgroup_id,
                  taskgroup_name: name
                }}>Edit </Link>
                <Link to='/taskgroups/move'>Move </Link>
                <Link onClick={() => deleteTaskGroup(taskgroup_id)}>Delete</Link>
              </td>
            </tr>
          ))}
      </table>

      <div id='tasksList'>
      {topic.taskgroups.map(({taskgroup_id, name, tasks}, index) => (
        <>
          <h3>{name}</h3>
          <div className='teacherButton'>
            <Link to={{
              pathname: '/tasks/add',
              taskgroup_id: taskgroup_id,
              topic_name: topic.name,
              taskgroup_name: name
            }}>New Task</Link>
          </div>
          <table id="taskgroup">
            <tr><td>Name</td><td>Difficulty</td><td>Actions</td></tr>
            {tasks.map(({task_id, name, difficulty}, index) => (
              <tr>
                <td><Link to={{
                  pathname: '/tasks/view',
                  task_id: task_id,
                  topic_name: topic.name,
                }}>{name}</Link></td>
                <td>{difficulty}</td>
                <td>
                  <Link to={{
                    pathname: '/tasks/edit',
                    task_id: task_id,
                    topic_name: topic.name
                  }}>Edit </Link>
                  <Link to={{
                    pathname: '/tasks/attachments',
                    task_id: task_id,
                    task_name: name,
                    topic_name: topic.name
                  }}>Attachments </Link>
                  <Link onClick={() => deleteTask(task_id)}>Delete</Link>
                </td>
              </tr>
            ))}
          </table>
        </>
      ))}
      </div>

    </>
  )
}


// <div className='teacherButton' id='reorderTasksButton'>Reorder Tasks</div>
export default Manage
