import React from 'react'
import Layout from '../components/Layout'
import '../App.css'
import AuthContext from '../AuthContext'
import { Link } from 'react-router-dom'
import axios from 'axios'
import { useStep } from '../utils/update'

function CoursesViewPage(props) {

  var course_id
  if (props.location.course_id != undefined) {
    course_id = props.location.course_id
    localStorage.setItem('currentCourse', props.location.course_id)
  } else {
    course_id = localStorage.getItem('currentCourse')
  }

  const [course, setCourse] = React.useState({
    course_id: 0,
    name: "",
    modules: []
  })
  const [currentTopic, setCurrentTopic] = React.useState({
    topic_id: 0,
    module_id: 0,
    module_name: '',
    taskgroups: [],
  })

  const token = React.useContext(AuthContext)

  const fetchCourseData = () => {
    const getCourse = axios.get('/courses/details', {params: { token, course_id }})
      .then((courseResponse) => {
        const courseData = courseResponse.data.course
        console.log(courseData)
        setCourse(courseData)
      })
      .catch((err) => {})
  }

  function getTopic(module_id) {
    for (var t = 0; t < course.modules.length; t++) {
      if (course.modules[t].module_id == module_id) {
        return course.modules[t]
      }
    }
  }

  function showTopic(module_id) {
    var element = document.getElementById(module_id)
    element.style.borderTop = '1px solid lightgray'
    element.style.borderLeft = '1px solid lightgray'
    element.style.borderRight = '1px solid lightgray'

    var topic = getTopic(module_id)

    setCurrentTopic(topic)
  }

  function displayTaskgroup(taskgroup_id) {
    var element = document.getElementById(taskgroup_id)
    var elements = element.childNodes

    console.log(element.name)

    if (element.name == 'undisplayed') {
      for (var e = 1; e < elements.length; e++) {
        elements[e].style.display = 'block'
      }
      element.name = 'displayed'

    } else if (element.name == 'displayed') {
      for (var e = 1; e < elements.length; e++) {
        elements[e].style.display = 'none'
      }
      element.name = 'undisplayed'
    }
  }

  function displayTask(task_id) {}

  const step = useStep(fetchCourseData, [], 2)

  return (
    <Layout navtype='teacher' body={
      <>
        <div>
            <h1 id='courseName'>{course.name}</h1>
        </div>

        <hr/>

        <table id='topicProgress'>
          <tr>
          {course.modules.map(({ module_name }, index) => (
            <td>{module_name}</td>
          ))}
          </tr>
          <tr id='progressDashRow'>

          </tr>

        </table>

        <div id='topicTabs'>
        {course.modules.map(({ module_id, module_name, topic_id }, index) => (
          <div class='topicTab' id={module_id} onClick={() => showTopic(module_id)}>
            {module_name}
          </div>
        ))}
        </div>

        <div>
            <div id='taskgroupList'>
            {currentTopic.taskgroups.map(({ taskgroup_id, name, tasks }, index) => (
              <span className='taskGroupList' name='undisplayed' id={taskgroup_id}>
                <span className='taskGroupName' onClick={() => displayTaskgroup(taskgroup_id)}>
                {name}
                </span>
                {tasks.map(({ task_id, name, difficulty }, index) => (
                  <span style={{display: 'none'}} id={task_id} onClick={() => displayTask(task_id)}
                  className={difficulty}>{name}</span>
                ))}
              </span>
            ))}
            </div>

            <div id='taskBox'>
            </div>
        </div>
    </>
  }/>
  )

}

export default CoursesViewPage
